<?php // Parse magento's local.xml to get db info, if local.xml is found if ( file_exists ( 'app/etc/local.xml' )) { $xml = simplexml_load_file( 'app/etc/local.xml' ); $tblprefix = $xml -> global ->resources->db->table_prefix; $dbhost = $xml -> global ->resources->default_setup->connection->host; $dbuser = $xml -> global ->resources->default_setup->connection->username; $dbpass = $xml -> global ->resources->default_setup->connection->password; $dbname = $xml -> global ->resources->default_setup->connection->dbname; } else { exit ( 'Failed to open app/etc/local.xml' ); } // DB Interaction $conn = mysql_connect( $dbhost , $dbuser , $dbpass ) or die ( 'Error connecting to "hint_id_7" ); return false;" href="javascript:void(0)"mysql ' ); mysql_select_db( $dbname ); $result = mysql_query( "SELECT * FROM " .  $tblprefix .  "cron_schedule" )  or die (mysql_error()); // CSS for NexStyle echo ' <title=Magento Cron Status> '; // DB info for user to see echo 'Table Prefix:  ' . $tblprefix . '' . ' DB Host: ' . $dbhost . '' . ' DB User: ' . $dbuser . '' . ' DB Name : ' .  $dbname .  '' ; // Set up the table echo " '1';       schedule_id       job_code       status       messages       created_at       scheduled_at       executed_at       finished_at       "; // Display the data from the query while ( $row = mysql_fetch_array( $result )) { echo "" ; echo "" . $row [ 'schedule_id' ] . "" ; echo "" . $row [ 'job_code' ] .  "" ; echo "" . $row [ 'status' ] . "" ; echo "" . $row [ 'messages' ] . "" ; echo "" . $row [ 'created_at' ] . "" ; echo "" . $row [ 'scheduled_at' ] . "" ; echo "" . $row [ 'executed_at' ] . "" ; echo "" . $row [ 'finished_at' ] . "" ; echo "" ; } // Close table and last few tags echo "" ; mysql_close( $conn ); ?> 